import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import {makeStyles} from '@material-ui/core/styles';
import clsx from 'clsx';
import React, {ReactNode} from 'react';
import 'rsuite/dist/styles/rsuite-default.css';
import config from './components/config/defaultblueprint.json';
import SocialIcon2 from './components/SocialIcon2';


type GridVprops ={
alignContent?:string;
alignItems?:string;
children?:	ReactNode;
className?:string;
classes?:	object;
component?: Element;
container?:	boolean;
direction?:string;
item?:boolean;
justify?:string;
wrap?:string;
spacing?:number;
xl?:boolean|string|number;
xs?:boolean|string|number;
md?:boolean|string|number;
lg?:boolean|string|number;
sm?:boolean|string|number;
zeroMinWidth?:	boolean;
}


const useStyles = makeStyles((theme) => ({
  paper: {
    textAlign: 'center',
    width: '25%',
    height: '125px',
    display:'flex',
    fontWeight:'lighter',
    fontSize:'12px',
  },
  leftBorderHeader: {
    padding: '0px 10px',
    borderLeft: '1px solid ',
    borderColor: config.companyInfo.borderColor,
  },

 logoMargin:{
    margin:'0px 15px'
 },
 copyRight:{
   display:'flex',
   fontSize:'10px'
 }
}));

type FooterVprops = {
    containerBackgroundColor?:string;
    textColor?:string;
    borderColor?:string;
    companyLink?:string;
    logoSrc?:string;
    logoAlt?:string;
    address?:string;
    copyright?:string;
    companyName?:string;
    contactNumber?:string;
    email?:string;
    footerdata?:any[];
    backGround?:any;
};


  export const FooterV=(props:FooterVprops)=>{
  const output: any = [];

  const classes = useStyles();

  const getfooter = () => {
    // eslint-disable-next-line no-unused-expressions
    Array.isArray(props.footerdata) &&
      props.footerdata.forEach(items => {
        output.push(

       <div style={props.backGround}>
       <Grid container spacing={0}  >
         <Grid item lg={3} justify="center" alignItems="center" className={classes.paper}>
                <a href={items.companyLink}><img src={items.logoSrc} alt={items.logoAlt} /></a>
        </Grid>
      <Grid item lg={3} justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)} >
           {items.address}
        </Grid>
       <Grid item lg={3} justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)} >
            {items.email}
            <br/>
             {items.contactNumber}
        </Grid>
        <Grid item lg={3}  justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)}>
           <SocialIcon2/>
        </Grid>
         </Grid>
          <hr/>
      <Grid item lg={12} justify="center" alignItems="center" className={classes.copyRight}>
          <h6>{items.copyright} <a href={items.companyLink}>{items.companyName}</a></h6>
       </Grid>
       </div>
        );
      });
    return output;
  };

  const _gridprops = {...props};
  return <Container {..._gridprops}>{ getfooter()}</Container>;

  };
