import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import {makeStyles} from '@material-ui/core/styles';
import clsx from 'clsx';
import React, {ReactNode} from 'react';
import {Icon} from 'rsuite';
import 'rsuite/dist/styles/rsuite-default.css';
import config from './components/config/defaultblueprint.json';


type ContainerVprops = {
  classes?:	object;
  className?:string;
  component?: Element;
  disableGutters?: boolean;
  maxWidth?: string;
  fixed?:	boolean;
  children?: any;
};

type GridVprops ={
alignContent?:string;
alignItems?:string;
children?:	ReactNode;
className?:string;
classes?:	object;
component?: Element;
container?:	boolean;
direction?:string;
item?:boolean;
justify?:string;
wrap?:string;
spacing?:number;
xl?:boolean|string|number;
xs?:boolean|string|number;
md?:boolean|string|number;
lg?:boolean|string|number;
sm?:boolean|string|number;
zeroMinWidth?:	boolean;
}

 const ContainerV = (props: ContainerVprops) => {
  const _containerprops = {...props};
  return <Container {..._containerprops}>{props.children}</Container>;
};

 const GridV = (props: GridVprops) => {
  const _gridprops = {...props};
  return <Grid {..._gridprops}>{props.children}</Grid>;
};


const useStyles = makeStyles((theme) => ({
  paper: {
    textAlign: 'center',
    width: '25%',
    height: '125px',
    display:'flex',
    fontWeight:'lighter',
    fontSize:'12px',
  },
  leftBorderHeader: {
    padding: '0px 10px',
    borderLeft: '1px solid ',
    borderColor: 'grey',
  },

 logoMargin:{
    margin:'0px 15px'
 },
 copyRight:{
   display:'flex',
   fontSize:'10px'
 }
}));

type FooterVprops = {
    containerBackgroundColor?:string;
    textColor?:string;
    borderColor?:string;
    companyLink?:string;
    logoSrc?:string;
    logoAlt?:string;
    address?:string;
    copyright?:string;
    companyName?:string;
    contactNumber?:string;
    email?:string;
};

 export const FooterV=(props)=>{
   const arr = props.socialicon;

     const classes = useStyles();

     const containerStyle={
     backgroundColor: props.containerBackgroundColor,
     color: props.textColor,
     margin:"10px auto",
     height: "200px"
    }

       return(
        <ContainerV style={containerStyle} maxWidth="lg" >
       <GridV container spacing={0}  >
         <GridV item lg={3} justify="center" alignItems="center" className={classes.paper}>
                <a href={props.companyLink}><img src={props.logoSrc} alt={props.logoAlt} /></a>
        </GridV>
      <GridV item lg={3} justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)} >
           {props.address}
        </GridV>
       <GridV item lg={3} justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)} >
            {props.email}
            <br/>
             {props.contactNumber}
        </GridV>
        <GridV item lg={3}  justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)}>
       {arr.map(element=>{

           <a href={element.url} target={element.target}>
             <Icon icon={element.icon} size="2x" />
            </a>  })}

        </GridV>
         </GridV>
          <hr/>
      <GridV item lg={12} justify="center" alignItems="center" className={classes.copyRight}>
          <h6>{props.copyright} <a href={props.companyLink}>{config.companyInfo.companyName}</a></h6>
       </GridV>
       </ContainerV>

       )
};
