import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import { makeStyles } from '@material-ui/core/styles';
import config from './src/components/config/defaultblueprint.json'
import clsx from  'clsx';
import SocialIcon2 from './src/components/SocialIcon2'
import {ContainerV, GridV} from './src';

const useStyles = makeStyles((theme) => ({
  root: {
     backgroundColor: config.companyInfo.containerBackgroundColor,
     color: config.companyInfo.textColor,
     margin:'10px auto',
     height: '200px'

  },
  paper: {
    textAlign: 'center',
    width: '25%',
    height: '125px',
    display:'flex',
    fontWeight:'lighter',
    fontSize:'12px',
  },
  leftBorderHeader: {
    padding: '0px 10px',
    borderLeft: '1px solid ',
    borderColor: config.companyInfo.borderColor,
  },

 logoMargin:{
    margin:'0px 15px'
 },
 copyRight:{
   display:'flex',
   fontSize:'10px'
 }
}));

 function SimpleContainer(){

     const classes = useStyles();

  return (

    <React.Fragment>

      <CssBaseline />
      <ContainerV maxWidth="lg" className={classes.root} >
        <GridV container spacing={0}  >

        <GridV item lg={3} justify="center" alignItems="center" className={classes.paper}>
                <a href={config.companyInfo.companyLink}><img src={config.companyInfo.logoSrc} alt={config.companyInfo.logoAlt} /></a>
        </GridV>
       <GridV item lg={3} justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)} >
            {config.companyInfo.address}
        </GridV>
        <GridV item lg={3}  justify="center" alignItems="center"  className={clsx(classes.paper,classes.leftBorderHeader)}>
            {config.companyInfo.email}
          <br/>
            {config.companyInfo.contactNumber}
        </GridV>

        <GridV item lg={3} alignContent="flex-center" justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)}>
          <SocialIcon2 />

        </GridV>

      </GridV>
      <hr/>
      <GridV item lg={12} justify="center" alignItems="center" className={classes.copyRight}>
          <h6>{config.companyInfo.copyright} <a href={config.companyInfo.companyLink}>{config.companyInfo.companyName}</a></h6>
       </GridV>
      </ContainerV>

    </React.Fragment>
  );
}
export default SimpleContainer;
