//@ts-nocheck
import {ResponsiveRadar} from '@nivo/radar';
import React from 'react';
import config from './config/defaultblueprint.json';

const bp=config.chartInfo.RadarData

export type RadarDataProps = {
    RadarData:any
  }

const Radar = (props:RadarDataProps) => {
  return(
    <ResponsiveRadar
        data={props?.RadarData}
        keys={[ 'chardonay', 'carmenere', 'syrah' ]}
        indexBy="taste"
        maxValue="auto"
        margin={{ top: 70, right: 80, bottom: 40, left: 80 }}
        curve="linearClosed"
        borderWidth={2}
        borderColor={{ from: 'color', modifiers: [] }}
        gridLevels={5}
        gridShape="circular"
        gridLabelOffset={36}
        enableDots={true}
        dotSize={9}
        dotColor={{ theme: 'background' }}
        dotBorderWidth={2}
        dotBorderColor={{ from: 'color' }}
        enableDotLabel={true}
        dotLabel="value"
        dotLabelYOffset={-12}
        colors={{ scheme: 'paired' }}
        fillOpacity={0.6}
        blendMode="multiply"
        animate={true}
        motionConfig="wobbly"
        isInteractive={true}
        legends={[
            {
                anchor: 'bottom',
                direction: 'row',
                translateX: -50,
                translateY: -40,
                itemWidth: 100,
                itemHeight: 90,
                itemTextColor: '#999',
                symbolSize: 12,
                symbolShape: 'circle',
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemTextColor: '#000'
                        }
                    }
                ]
            }
        ]}
    />
)
      }

export default Radar;
