import Container from '@material-ui/core/Container';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import {makeStyles} from '@material-ui/core/styles';
import clsx from 'clsx';
import React, {ReactNode} from 'react';
import 'rsuite/dist/styles/rsuite-default.css';
import config from './components/config/defaultblueprint.json';


type ContainerVprops = {
  classes?:	object;
  className?:string;
  component?: Element;
  disableGutters?: boolean;
  maxWidth?: string;
  fixed?:	boolean;
  children?: any;
};

type GridVprops ={
alignContent?:string;
alignItems?:string;
children?:	ReactNode;
className?:string;
classes?:	object;
 component?: Element;
container?:	boolean;
direction?:string;
item?:boolean;
justify?:string;
wrap?:string;
spacing?:number;
xl?:boolean|string|number;
xs?:boolean|string|number;
md?:boolean|string|number;
lg?:boolean|string|number;
sm?:boolean|string|number;
zeroMinWidth?:	boolean;
}

 const ContainerV = (props: ContainerVprops) => {
  const _containerprops = {...props};
  return <Container {..._containerprops}>{props.children}</Container>;
};

 const GridV = (props: GridVprops) => {
  const _gridprops = {...props};
  return <Grid {..._gridprops}>{props.children}</Grid>;
};


const useStyles = makeStyles((theme) => ({
  root: {
     backgroundColor: config.companyInfo.containerBackgroundColor,
     color: config.companyInfo.textColor,
     margin:'10px auto',
     height: '200px'
  },
  paper: {
    textAlign: 'center',
    width: '25%',
    height: '125px',
    display:'flex',
    fontWeight:'lighter',
    fontSize:'12px',
  },
  leftBorderHeader: {
    padding: '0px 10px',
    borderLeft: '1px solid ',
    borderColor: config.companyInfo.borderColor,
  },

 logoMargin:{
    margin:'0px 15px'
 },
 copyRight:{
   display:'flex',
   fontSize:'10px'
 }
}));

 export const FooterV=(props)=>{
     const classes = useStyles();

     const Address:any = [
      { containerBackgroundColor:"dimgrey",
    textColor:"#eee",
    borderColor:"grey",
    companyLink:"https://www.vlinder.io/",
    logoSrc:"https://i.ibb.co/k6wXVbg/IMG-20201014-WA0026.jpg",
    logoAlt:"vlinder.io",
    address:"vlinder Labs Pvt. Ltd. TERRACE FLOOR, 837, 11TH CROSS, 26th Main Rd, 1st Sector, HSR Layout, Bengaluru, Karnataka 560102",
    copyright:"copyright: 2020 vlinder inc. All rights reserved. powered by",
    companyName:"vlinder.io",
    socialicon:props.isSocialicon,
    contactinfo:props.isContactinfo
      }
     ]

     const arr = Address.map(element => {
       return(
        <ContainerV className={classes.root} maxWidth="lg" >
       <GridV container spacing={0}  >
         <GridV item lg={3} justify="center" alignItems="center" className={classes.paper}>
                <a href={element.companyLink}><img src={element.logoSrc} alt={element.logoAlt} /></a>
        </GridV>

      <GridV item lg={3} justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)} >
            {element.address}
        </GridV>
        <GridV item lg={3}  justify="center" alignItems="center"  className={clsx(classes.paper,classes.leftBorderHeader)}>
           {element.contactinfo}
        </GridV>
        <GridV item lg={3}  justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)}>
           {element.socialicon}
        </GridV>
         </GridV>
          <hr/>
      <GridV item lg={12} justify="center" alignItems="center" className={classes.copyRight}>
          <h6>{element.copyright} <a href={element.companyLink}>{config.companyInfo.companyName}</a></h6>
       </GridV>
       </ContainerV>

       )
     });


  return (

    <React.Fragment>
      <CssBaseline />
         {arr}
    </React.Fragment>

  );
};
