import Grid from '@material-ui/core/Grid';
import React from 'react';
import {StyleSheet, View} from 'react-native';
import BarChart from './components/BarChart';
import {default as CardDisplayV} from './components/cards';
import DonutChartV from './components/donutChart';
import Header from './components/headerV';
import LineChartV from './components/LineChart';
import NavbarV from './components/Navbar';
import RadarChart from './components/RadarChart';
import SmallCardLayout from './components/SmallCardLayout';
import TimeLineV from './components/timelineV';

const Mystyle1={
  backgroundColor:{
    backgroundColor:'#eee'
  },
  chartstyle:{
    backgroundColor:'white',
    margin:'10px',
    borderRadius:'5px',
    padding:'10px',
    boxShadow: '2px 2px #ccc',
    marginLeft:"200px"
  },
  containerPos:{
    marginLeft:"-45px"
  }
}




export type dashBoardProps= {
  graphData?:any;
  // tinyLineChartData:any;

  // tinyBarChartData:any;
  // tinyAreaChartData:any;
  // AnglePieData:any;
  // RadarData:any;
  // PieDonutChartData:any;
  // BarChartData:any;
  // timelinedata:any;

}


const Mystyle={
  backgroundColor:{
    backgroundColor:'#eee'
  },
  chartstyle:{
    backgroundColor:'white',

    marginLeft:'65px',
    borderRadius:'5px',
    padding:'10px',

  },
  timeline :{
    borderRadius:'5px'

  },
  containerPos:{
    marginLeft:"-45px"
  }
}





const  DashboardV=(props: dashBoardProps)=> {
  const _getSmallCard= ()=> {
    const smallOutput:any =[];
    Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
      smallOutput.push(
        <Grid container>

          <Grid item md={items?.smallcardlayout?.md}>
          <SmallCardLayout

             tinyBarChartData={items?.smallcardlayout?.tinybarchart}
             tinyLineChartData={items?.smallcardlayout?.tinylinechart}
               tinyAreaChartData={items?.smallcardlayout?.tinyareachart}
                AnglePieData={items?.smallcardlayout?.anglepie}
        />
          </Grid>

                </Grid>
      )
    });
    return smallOutput;

  }

    const getLineChart = () =>{
      const output: any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items.type === "lineChartData")
        {
        output.push(
            <Grid item md={items?.md} sm={items?.sm}style={StyleSheet.flatten([items?.styles])} >
              <LineChartV
                lineChartData={items?.data}  />
            </Grid>
        )
        }
      });
      return output;
    }


    const getTimeline= () =>{
      const output: any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items.type === "timelinedata")
        {
        output.push(
            <Grid item md={items?.md} sm={items?.sm} style={StyleSheet.flatten([items?.styles])} >
              <TimeLineV
              timelinedata={items?.dataset}/>


            </Grid>
        )
        }
      });
      return output;
    }








    const _getRadarChart= ()=> {
      const cardsecoutput:any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items?.type === "radarchart") {

          cardsecoutput.push(

            <Grid item xs={12} sm={9} md={items?.md} style={StyleSheet.flatten([items?.styles])}>
<RadarChart RadarChartData={items?.data}/>


    </Grid>
        )
      }
      });
      return cardsecoutput;

    }


    const getdonutChart = () =>{
      const output: any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items?.type === "donutchart")
        output.push(
          <Grid item xs={12} sm={9} md={items?.md} style={StyleSheet.flatten([items?.styles])}>
            <DonutChartV
            donutchartdata={items?.data}/>
                </Grid>
        )
      });
      return output;
    }

    const getbarChart = () =>{
      const output: any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items?.type === "barchart")
        output.push(
          <Grid item xs={12} sm={9} md={items?.md} style={StyleSheet.flatten([items?.styles])}>
            <BarChart
            BarChartData={items?.data}
            barChartOptions={items?.data?.barChartOptions}/>
                </Grid>
        )
      });
      return output;
    }


    const getcard = () =>{
      const output: any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items?.type === "cardData")
        output.push(
          <Grid container >
            <CardDisplayV
            cardData={items?.data}/>
                </Grid>
        )
      });
      return output;
    }




  return (
    // <div>
    //   {_getSmallCard()}
    //   {getLineChart()}
    //   {_getCardSection()}

    // </div>
    <View style={StyleSheet.flatten([Mystyle.backgroundColor])}>
      <Header/>
      <Grid container  >


        <Grid item xs={12} sm={9} md={1}>
        <Grid item xs={12} sm={9} md={3} >
        <NavbarV/>
        </Grid>
        </Grid>

        <Grid item xs={12} sm={9} md={11} style={StyleSheet.flatten([Mystyle.containerPos])} >
        <Grid container  >
        <Grid item xs={11} sm={9} md={12} style={StyleSheet.flatten([Mystyle.backgroundColor])}>
          {getcard()}

      </Grid>
      <Grid container style={{marginLeft:"40px"}}>

          {getTimeline()}



      {getLineChart()}
        </Grid>

      </Grid>
      <Grid item xs={12} sm={9} md={8} >

      {/* <CardSection2/> */}
      </Grid>

    <Grid container spacing={10} style={{marginLeft:"40px"}}>

    {_getRadarChart()}
        {getdonutChart()}
        {getbarChart()}
    </Grid>


     </Grid>


      </Grid>

</View>



  );
}

export default DashboardV;
