import Paper from '@material-ui/core/Paper';
import {createStyles, makeStyles, Theme} from '@material-ui/core/styles';
import React from 'react';
import TinyBarChart from './TinyBarChart';

export type SmallCardLayoutProps={
  tinyLineChartData?:any
   tinyBarChartData?:any
   tinyAreaChartData:any
   AnglePieData:any
   graphData?:any
   smallcardlayout?:any;
}



const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      '& > *': {
        margin: "38px",
        width: "350px",
        height: "180px",
        paddingTop:"35px",
        borderRadius:"5px"
      },
    },
  }),
);



const simplePaper = (props: SmallCardLayoutProps)=>{
  const classes= useStyles();
  const _getCharts= ()=> {
    const chartOutput:any =[];
    Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
      chartOutput.push(


        <TinyBarChart
              tinyBarChartData={items?.smallcardlayout.tinybarchart}/>


      )
    });
    return chartOutput;
  }

  return (
    <div className={classes.root}>
       <Paper elevation={10}
      style={{
        backgroundColor:"white",
        textAlign: 'center',
        boxShadow: '10px 15px 15px rgba(100,100,255,0.3)',
      }}>{_getCharts()}</Paper>
    </div>
  )

}

