import Grid from '@material-ui/core/Grid';
import React from 'react';
import {StyleSheet, View} from 'react-native';
import CardDisplayV from './cards';
import HeaderV from './Header';
import LineChartV from './LineChart';
import NavbarV from './Navbar';

const Mystyle={
  backgroundColor:{
    backgroundColor:'#eee'
  },
  chartstyle:{
    backgroundColor:'white',
    margin:'10px',
    borderRadius:'5px',
    padding:'10px',
    boxShadow: '2px 2px #ccc'
  }
}

const cardData=[{
  backgroundColor:"linear-gradient(to top right, #1A329E, #95A6EE)",
  icon:"envelope",
  header:"01.",
  title:"Optimize Processing",
  description:"Simplify"
},
{
  backgroundColor:"linear-gradient(to right, #cdeb8e 0%,#a5c956 80%)",
  icon:"envelope",
  header:"02.",
  title:"Optimize Processing",
  description:"Simplify"},
{
  backgroundColor:"linear-gradient(to right, #34e89e ,#0f3443 -50% )",
  icon:"envelope",
  header:"03.",
  title:"Optimize Processing",
  description:"Simplify"},
{
  backgroundColor:"linear-gradient(to right,#c33764 0%, #1d2671 1%)",
  icon:"envelope",
  header:"04.",
  title:"Optimize Processing",
  description:"Simplify"}];





const ContentHeaderStyles={
  display:'flex',

  fontWeight:'900',
  color:'orange',
  fontSize:'80px',


}

const ContentTitleStyles={
  display:'flex',
  fontWeight:'500',
  color:'smokeywhite',
  textTransform: 'uppercase',
  fontSize:'20px',

}

const ContentIconStyles={
  width:'50px',
  height:'50px',
  float:"right",
  fontSize:"50px"
}

const ContentDescriptionStyles={
  display:'flex',
  fontWeight:'300',
  fontSize:'20px',


}


function DashboardV() {
  return (
    <View style={StyleSheet.flatten([Mystyle.backgroundColor])}>

      <HeaderV/>

      <Grid container  >
        <Grid item xs={12} sm={9} md={1}>
        <Grid item xs={12} sm={9} md={3} >
        <NavbarV/>
        </Grid>
        </Grid>
        <Grid item xs={12} sm={9} md={11} style={{marginLeft:"-100px"}}>
        <Grid container  >
        <Grid item xs={11} sm={9} md={12} style={StyleSheet.flatten([Mystyle.chartstyle])}>
        <CardDisplayV

cardData={cardData}
ContentHeaderStyles={ContentHeaderStyles}
ContentTitleStyles={ContentTitleStyles}
ContentIconStyles={ContentIconStyles}
ContentDescriptionStyles={ContentDescriptionStyles}/>
      </Grid>
        <Grid item xs={11} sm={9} md={12} style={StyleSheet.flatten([Mystyle.chartstyle])}>
      <LineChartV/>
      </Grid>


     </Grid>



      </Grid>


          </Grid>

</View>


  );
}

export default DashboardV;
