//@ts-nocheck
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import React from 'react';
import {StyleSheet, Text} from 'react-native';


type priceCardprops={

  planNameStylesStyles?:any
  amountStyles?:any
  buttonStyles?:any
  buttonPosition?:any
  listStyles?:any
  pricingTable:any[]
}

export const PriceCardDisplayV = (props:priceCardprops) => {
const {
        planNameStylesStyles,
        amountStyles,
        buttonStyles,
        buttonPosition,
        listStyles,
        pricingTable  } = props;

  const _getServices=(list:any[])=>{
    const _data:any=[];
     // eslint-disable-next-line no-unused-expressions
    Array.isArray(list) && list.forEach(item=>{
      _data.push(
        <ul  style={StyleSheet.flatten([listStyles])}>
          <li >{item?.li}</li>
        </ul>
      )
     });
     return _data;
    }


  const _getPriceCards = () => {
    const _tempData:any = [];
  // eslint-disable-next-line no-unused-expressions
    Array.isArray(pricingTable) && pricingTable.forEach(item=>{
      _tempData.push(
        <Grid item xs={10} sm={6} md={3} >
          <Paper  style={{backgroundImage:item?.backgroundColor,height:'100%', marginBottom:20}} >
            <header>
              <Text style={StyleSheet.flatten([planNameStylesStyles])}>{item?.title}</Text>
            </header>

            <hr/>
             {_getServices(item?.list)}
            <Text style={StyleSheet.flatten([amountStyles])}>{item?.amount}</Text>
            <footer style={StyleSheet.flatten([buttonPosition])} >
              <button style={StyleSheet.flatten([buttonStyles])}>{item?.buttonTitle}
            </button>
            </footer>
            <div>
            </div>
           </Paper>
        </Grid>
      )
     });
    return _tempData;
  }

  return(
    <Grid container justify={"center"} alignItems={"center"}  >
      <Grid item xs={11}>
      <Grid container justify={"center"} alignItems={"stretch"} spacing={4} >
      {_getPriceCards()}
      </Grid>
      </Grid>
    </Grid>
  )
}
