import Grid from '@material-ui/core/Grid';
import React from 'react';
import {StyleSheet, View} from 'react-native';
import CardDisplayV from './cards';
import HeaderV from './Header';
import LineChartV from './LineChart';
import NavbarV from './Navbar';

const Mystyle={
  backgroundColor:{
    backgroundColor:'#eee'
  },
  chartstyle:{
    backgroundColor:'white',
    margin:'10px',
    borderRadius:'5px',
    padding:'10px',
    boxShadow: '2px 2px #ccc'
  }
}

const cardData=[{
  backgroundColor:"linear-gradient(to top left , #4ac29a, #bdfff3)",
  icon:"envelope",
  header:"12.34",
  title:"Scans",
  description:"Simplify"
},
{
  backgroundColor:"linear-gradient(to bottom, #cdeb8e 0%,#a5c956 80%)",
  icon:"envelope",
  header:"4.2 ",
  title:"Ratings",
  description:"Simplify"},
{
  backgroundColor:"linear-gradient(to top, #654ea3, #eaafc8)",
  icon:"envelope",
  header:"89.01",
  title:"Reviews",
  description:"Simplify"},
{
  backgroundColor:"linear-gradient(to bottom,#4ecdc4 , #556270 )",
  icon:"envelope",
  header:"23.45",
  title:"Users",
  description:"Simplify"}];





const ContentHeaderStyles={
  display:'flex',


  color:'#f5f5f5',
  fontSize:'70px',


}

const ContentTitleStyles={
  display:'flex',
  fontWeight:'500',
  color:'smokeywhite',
  textTransform: 'uppercase',
  fontSize:'20px',

}

const ContentIconStyles={
  width:'50px',
  height:'50px',
  float:"right",
  fontSize:"50px",
  color:"#f5f5f5",
  opacity:0.5
}

const ContentDescriptionStyles={
  display:'flex',
  fontWeight:'350',
  fontSize:'20px',


}


function DashboardV() {
  return (
    <View style={StyleSheet.flatten([Mystyle.backgroundColor])}>

      <HeaderV/>

      <Grid container  >
        <Grid item xs={12} sm={9} md={1}>
        <Grid item xs={12} sm={9} md={3} >
        <NavbarV/>
        </Grid>
        </Grid>
        <Grid item xs={12} sm={9} md={11} style={{marginLeft:"-100px"}}>
        <Grid container  >
        <Grid item xs={11} sm={9} md={12} style={StyleSheet.flatten([Mystyle.chartstyle])}>
        <CardDisplayV

cardData={cardData}
ContentHeaderStyles={ContentHeaderStyles}
ContentTitleStyles={ContentTitleStyles}
ContentIconStyles={ContentIconStyles}
ContentDescriptionStyles={ContentDescriptionStyles}/>
      </Grid>
        <Grid item xs={11} sm={9} md={12} style={StyleSheet.flatten([Mystyle.chartstyle])}>
      <LineChartV/>
      </Grid>


     </Grid>



      </Grid>


          </Grid>

</View>


  );
}

export default DashboardV;
