import Grid from '@material-ui/core/Grid';
import React from 'react';
import {StyleSheet, View} from 'react-native';
import {default as CardDisplayV, default as Cards} from './components/cards';
import CardSection3 from './components/card_section3';
import Header from './components/headerV';
import LineChartV from './components/LineChart';
import NavbarV from './components/Navbar';
import SmallCardLayout from './components/SmallCardLayout';


const Mystyle1={
  backgroundColor:{
    backgroundColor:'#eee'
  },
  chartstyle:{
    backgroundColor:'white',
    margin:'10px',
    borderRadius:'5px',
    padding:'10px',
    boxShadow: '2px 2px #ccc',
    marginLeft:"200px"
  },
  containerPos:{
    marginLeft:"-45px"
  }
}




export type dashBoardProps= {
  graphData?:any;
  // tinyLineChartData:any;

  // tinyBarChartData:any;
  // tinyAreaChartData:any;
  // AnglePieData:any;
  // RadarData:any;
  // PieDonutChartData:any;
  // BarChartData:any;
  // timelinedata:any;

}


const Mystyle={
  backgroundColor:{
    backgroundColor:'#eee'
  },
  chartstyle:{
    backgroundColor:'white',

    marginLeft:'65px',
    borderRadius:'5px',
    padding:'10px',

  },
  timeline :{
    borderRadius:'5px'

  },
  containerPos:{
    marginLeft:"-45px"
  }
}





const  DashboardV=(props: dashBoardProps)=> {
  const _getSmallCard= ()=> {
    const smallOutput:any =[];
    Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
      smallOutput.push(
        <Grid container>

          <Grid item md={items?.smallcardlayout?.md}>
          <SmallCardLayout

             tinyBarChartData={items?.smallcardlayout?.tinybarchart}
             tinyLineChartData={items?.smallcardlayout?.tinylinechart}
               tinyAreaChartData={items?.smallcardlayout?.tinyareachart}
                AnglePieData={items?.smallcardlayout?.anglepie}
        />
          </Grid>

                </Grid>
      )
    });
    return smallOutput;

  }

    const getLineChart = () =>{
      const output: any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items.type === "lineChartData")
        {
        output.push(
            <Grid item md={items?.md} sm={items?.sm}style={StyleSheet.flatten([items?.styles])} >
              <LineChartV
                lineChartData={items?.data}  />
            </Grid>
        )
        }
      });
      return output;
    }



    const _getCardSection= ()=> {
      const cardsecoutput:any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        cardsecoutput.push(

<Grid item xs={12} sm={9} md={12}>
  <CardSection3
  RadarChartData={items?.cardsection3?.radarchart}
  AreaChartData={items?.cardsection3?.areachart?.dataset}
  BarChartData={items?.cardsection3?.barchart?.dataset}
  />
    </Grid>
        )
      });
      return cardsecoutput;

    }



    const getcard = () =>{
      const output: any =[];
      Array.isArray(props?.graphData) && props?.graphData.forEach(items=>{
        if(items?.type === "cardData")
        output.push(
          <Grid container>
            <CardDisplayV
            cardData={items?.data}/>
                </Grid>
        )
      });
      return output;
    }


  return (
    // <div>
    //   {_getSmallCard()}
    //   {getLineChart()}
    //   {_getCardSection()}

    // </div>
    <View style={StyleSheet.flatten([Mystyle.backgroundColor])}>
      <Header/>
      <Grid container  >

        <Grid item xs={12} sm={9} md={11} style={StyleSheet.flatten([Mystyle1.containerPos])} >
        <Grid container  >
        <Grid item xs={11} sm={9} md={12} style={StyleSheet.flatten([Mystyle1.chartstyle])}>
      {getcard()}
      </Grid>

     </Grid>
      </Grid>
          </Grid>

      <Grid container  >
        <Grid item xs={12} sm={9} md={1}>
        <Grid item xs={12} sm={9} md={3} >
        <NavbarV/>
        </Grid>
        </Grid>
        <Grid item xs={12} sm={9} md={11} style={StyleSheet.flatten([Mystyle.containerPos])} >
        <Grid container  >
        <Grid item xs={11} sm={9} md={12} style={StyleSheet.flatten([Mystyle.backgroundColor])}>

        <Cards />
      </Grid>
      <Grid container style={{marginLeft:"40px"}}>

      {getLineChart()}

      </Grid>
      <Grid item xs={12} sm={9} md={8} >

      {/* <CardSection2/> */}
      </Grid>

    <Grid item xs={12} sm={9} md={12}>
     {_getCardSection()}
    </Grid>

     </Grid>

      </Grid>
          </Grid>
</View>



  );
}
export default DashboardV;
