//@ts-nocheck
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import React from 'react';
import {StyleSheet, Text} from 'react-native';
import {Icon} from 'rsuite';



const styles = {



  paper: {
    padding: 10,
    textAlign: 'center',

    // background:"linear-gradient(to right, #1e5799 0%,#207cca 25%,#207cca 25%,#2989d8 50%,#7db9e8 100%)",
    width:"340px",
    height:"180px",
    boxShadow:"2px 10px 10px rgba(100,100,255,0.3)"
  },
  bg :{

  }

}

type cardprops={
  title?: string
  description?:string
  children?: React.ReactNode
  cardData?: {header?:string, title?: string,description?: string,icon?:string,backgroundColor?:string}[]
  titleStyles?:any
  subtitleStyles?:any
  ContentHeaderStyles?:any
  ContentTitleStyles?:any
  ContentDescriptionStyles?:any
  ContentIconStyles?:any


}

 const CardDisplayV = (props:cardprops) => {
  const _getCards = () => {


    const _tempData:any = [];
  // eslint-disable-next-line no-unused-expressions
    Array.isArray(props?.cardData) && props?.cardData.forEach(item=>{
      const i= 0
      _tempData.push(
        <Grid item xs={12} sm={6} md={3}>
        <Paper  style={styles.paper,{backgroundImage:item?.backgroundColor}} >
           <Icon style={StyleSheet.flatten([styles.ContentStyles, props?.ContentIconStyles])} icon={item.icon}/>
          <Text style={StyleSheet.flatten([styles.ContentStyles, props?.ContentTitleStyles])}>{item?.title}</Text>
          <Text style={StyleSheet.flatten([styles.ContentStyles, props?.ContentDescriptionStyles])}>{item?.description}</Text>
          <Text style={StyleSheet.flatten([styles.ContentStyles, props?.ContentHeaderStyles])}>{item?.header}</Text>
        </Paper>
     </Grid>
      )
    });
    return _tempData;
  }

  return(
    <Grid container justify={"center"} alignItems={"center"} >


      <Grid container   spacing={3} style={{background:"#eeee" }}>
      {_getCards()}

      </Grid>
    </Grid>
  )
}


export default CardDisplayV;
