//@ts-nocheck
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import React from 'react';
import {Text} from 'react-native';


const styles = {


  paper: {
    listStyles:'none',
    fontSize:'50px'
  }
}

type priceCardprops={
  // children?: React.ReactNode
  buttonText?:string
  servicesData?:any
  cardData?: any
  planNameStylesStyles?:any
  amountStyles?:any
  buttonStyles?:any
  buttonPosition?:any
  listStyles?:any
}

export const PriceCardDisplayV = (props:priceCardprops) => {
const { buttonText,
        servicesData,
        cardData ,
        planNameStylesStyles,
        amountStyles,
        buttonStyles,
        buttonPosition,
        listStyles  } = props;

  const _getServices=()=>{
    const _data:any=[];
     // eslint-disable-next-line no-unused-expressions
    Array.isArray(servicesData) && servicesData.forEach(item=>{
      _data.push(
        <ul style={listStyles}>
          <li >{item.name}</li>
        </ul>
      )
     });
     return _data;
    }
  const _getPriceCards = () => {


    const _tempData:any = [];
  // eslint-disable-next-line no-unused-expressions
    Array.isArray(cardData) && cardData.forEach(item=>{
      _tempData.push(
        <Grid item xs={12} sm={6} md={3} >
          <Paper  style={{backgroundImage:item.bg}} >
          <Text style={planNameStylesStyles}>{item?.planName}</Text>
            <hr/>
             {_getServices()}
            <Text style={amountStyles}>{item.amount}</Text>
            <div style={buttonPosition}>
            <button style={buttonStyles}>{buttonText}
            </button>
            </div>
           </Paper>
        </Grid>
      )
     });
    return _tempData;
  }

  return(
    <Grid container justify={"center"} alignItems={"center"}  >
      <Grid item xs={11}>
      <Grid container justify={"center"} alignItems={"center"} spacing={4} >
      {_getPriceCards()}
      </Grid>
      </Grid>
    </Grid>
  )
}
