import React from 'react';
import {PriceCardDisplayV} from './src/PriceCardWidget';


const pricingTable = [
  {
    "key": "standard-package",
    "header": "Standard",
    "title":"Great place to start small projects.",
    "amountTitle":"AS LOW AS",
    "amount":"$28 /month",
    "backgroundColor":"linear-gradient(to top right, #1A329E, #95A6EE)",
    "buttonTitle": "Start free trial",
    "bodyTitle":"Features include:",
    "bodyContent": [
      {
        "li": "Core Elastic Stack security features",
      },
      {
        "li": "Capabilities such as Elastic APM, App Search, Workplace Search, Security, and Maps",
      },
      {
        "li": "Canvas & Lens",
      },
      {
        "li": "And more"
      }
    ]
  },
  {
    "key": "gold-package",
    "header": "Gold",
    "title":"Dedicated support and more features.",
    "amountTitle":"AS LOW AS",
    "amount":"$40 /month",
    "backgroundColor":"linear-gradient(to top right, #1A329E, #95A6EE)",
    "buttonTitle": "Start free trial",
    "bodyTitle":"Everything in Standard plus:",
    "bodyContent": [
      {
        "li": "Custom plugins",
      },
      {
        "li": "Business hour support",
      },
      {
        "li": "And more",
      },

    ]
  },
  {
    "key": "platinum-package",
    "header": "Platinum",
    "title":"Advanced functionality with 24/7 support.",
    "amountTitle":"AS LOW AS",
    "amount":"$50 /month",
    "backgroundColor":"linear-gradient(to top right, #1A329E, #95A6EE)",
    "buttonTitle": "Start free trial",
    "bodyTitle":"Everything in Gold plus:",
    "bodyContent": [
      {
        "li": "Advanced Elastic Stack security features",
      },
      {
        "li": "Machine learning",
      },
      {
        "li": "24/7/365 support",
      },
      {
        "li": "And more"
      }
    ]
  },
  {
    "key": "enterprise-package",
    "header": "Enterprise",
    "title":"The fully loaded package with endpoint protection by default.",
    "amountTitle":"To Find Out More",
    "amount":"Contact us",
    "backgroundColor":"linear-gradient(to top right, #1A329E, #95A6EE)",
    "buttonTitle": "Contact us",
    "bodyTitle":"Everything in Platinum plus:",
    "bodyContent": [
      {
        "li": "Access to Elastic Endgame",
      },

    ]
  },

]


const headerStyles={
  display:'flex',
  justifyContent:'center',
  fontWeight:'normal',
  color:'white',
  fontSize:'25px',
  paddingTop:'10px',
  textTransform:'uppercase',
  marginBottom:'50px',
}

const titleStyles={
  display:'flex',
  justifyContent:'center',
  textAlign:'center',
  color:'white',
  fontSize:'14px',
  fontWeight:'350',
  paddingLeft:'15px',
  paddingRight:'15px',
  height:"35px",
  marginBottom:'30px'
  // marginTop:'5px',
}

const amountTitleStyles={
  display:'flex',
  justifyContent:'center',
  textAlign:'center',
  color:'#ccc',
  fontSize:'14px',
  fontWeight:'500',
}

const amountStyles={
  display:'flex',
  justifyContent:'center',
  fontWeight:'normal',
  color:'white',
  fontSize:'25px',
  paddingRight:'20px',
  paddingLeft:'20px',
  textAlign:'center',
  height:'20px',
  // marginTop:'25px',
  marginBottom:'50px'
}

const bodyTitleStyles={
  display:'flex',
  justifyContent:'flex-start',
  marginLeft:'40px',
  color:'#ccc',
  fontSize:'16px',
  fontWeight:'500',
}

const bodyHeight={
  height:"180px"
}


const listStyles={
  fontSize:"14px",
  color:'white',
  listStyleType:'none',
  textAlign:'left',
  paddingRight:'20px',
  fontWeight:'200',
}

const buttonStyles={
  fontSize:'20px',
  marginLeft:'10px',
  marginRight:'10px',
  marginBottom:'20px',
  borderRadius:'25px',
  backgroundColor:'white',
  paddingRight:'30px',
  paddingLeft:'30px',
  paddingTop:'5px',
  paddingBottom:'5px',
  color:'#008F69',

}

const buttonPosition={
  display:'flex',
  justifyContent:'center',
  alignItems:'center'
};


function App() {
  return (
    <div>
     <PriceCardDisplayV
     pricingTable={pricingTable}
     headerStyles={headerStyles}
     titleStyles={titleStyles}
     amountTitleStyles={amountTitleStyles}
     amountStyles={amountStyles}
     bodyTitleStyles={bodyTitleStyles}
     bodyHeight={bodyHeight}
     listStyles={listStyles}
     buttonStyles={buttonStyles}
     buttonPosition={buttonPosition}
     />
    </div>
  );
}

export default App;
