import Container from '@material-ui/core/Container';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import {makeStyles} from '@material-ui/core/styles';
import clsx from 'clsx';
import React, {ReactNode} from 'react';
import {Footer} from 'rsuite';
import 'rsuite/dist/styles/rsuite-default.css';
import config from './components/config/defaultblueprint.json';
import SocialIcon2 from './components/SocialIcon2';


type ContainerVprops = {
  classes?:	object;
  className?:string;
  component?: Element;
  disableGutters?: boolean;
  maxWidth?: string;
  fixed?:	boolean;
  children?: any;
};

type GridVprops ={
alignContent?:string;
alignItems?:string;
children?:	ReactNode;
className?:string;
classes?:	object;
 component?: Element;
container?:	boolean;
direction?:string;
item?:boolean;
justify?:string;
wrap?:string;
spacing?:number;
xl?:boolean|string|number;
xs?:boolean|string|number;
md?:boolean|string|number;
lg?:boolean|string|number;
sm?:boolean|string|number;
zeroMinWidth?:	boolean;
}

 const ContainerV = (props: ContainerVprops):JSX.Element => {
  const _containerprops = {...props};
  return <Container {..._containerprops}>{props.children}</Container>;
};

 const GridV = (props: GridVprops) => {
  const _gridprops = {...props};
  return <Grid {..._gridprops}>{props.children}</Grid>;
};


const useStyles = makeStyles((theme) => ({
  root: {
     backgroundColor: config.companyInfo.containerBackgroundColor,
     color: config.companyInfo.textColor,
     margin:'10px auto',
     height: '200px'
  },
  paper: {
    textAlign: 'center',
    width: '25%',
    height: '125px',
    display:'flex',
    fontWeight:'lighter',
    fontSize:'12px',
  },
  leftBorderHeader: {
    padding: '0px 10px',
    borderLeft: '1px solid ',
    borderColor: config.companyInfo.borderColor,
  },

 logoMargin:{
    margin:'0px 15px'
 },
 copyRight:{
   display:'flex',
   fontSize:'10px'
 }
}));

 export const FooterV=(props: ContainerVprops | GridVprops)=>{
 const _footerprops = {...props};
     const classes = useStyles();

  return (
    <Footer  {..._footerprops}>{
    <React.Fragment>
      <CssBaseline />
       <ContainerV className={classes.root} maxWidth="lg" >
       <GridV container spacing={0}  >

        <GridV item lg={3} justify="center" alignItems="center" className={classes.paper}>
                <a href={config.companyInfo.companyLink}><img src={config.companyInfo.logoSrc} alt={config.companyInfo.logoAlt} /></a>
        </GridV>
       <GridV item lg={3} justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)} >
            {config.companyInfo.address}
        </GridV>
        <GridV item lg={3}  justify="center" alignItems="center"  className={clsx(classes.paper,classes.leftBorderHeader)}>
            {config.companyInfo.email}
          <br/>
            {config.companyInfo.contactNumber}
        </GridV>
        <GridV item lg={3}  justify="center" alignItems="center" className={clsx(classes.paper,classes.leftBorderHeader)}>
          <SocialIcon2 />
        </GridV>

      </GridV>
      <hr/>
      <GridV item lg={12} justify="center" alignItems="center" className={classes.copyRight}>
          <h6>{config.companyInfo.copyright} <a href={config.companyInfo.companyLink}>{config.companyInfo.companyName}</a></h6>
       </GridV>
       </ContainerV>
    </React.Fragment>}
    </Footer>
  );
};
