import React, {Fragment} from 'react';
import {StyleSheet} from 'react-native';
import {Icon, Timeline} from 'rsuite';
import 'rsuite/dist/styles/rsuite-default.css';

//#4F84C4"

const useStyle: any = {
  arrowLeft: {
    width: '0',
    height: '0',

    borderTop: '20px inset transparent',
    borderBottom: '20px inset transparent',
    borderRight: '20px inset #4F84C4 ',

    marginLeft: '40px',
    position: 'absolute',
    filter: 'drop-shadow(0px 0px 1px rgba(0,0,0,2))',
  },
  customTimelineStyles : {
    marginLeft: '500px',
    marginTop: '100px',
  },
  contentStyles : {
    paddingLeft: '40px',
    paddingBottom: '20px',
    paddingTop: '10px',
    boxShadow: '0 0 5px rgba(0,83,156,1)',
    borderRadius: '5px',
    display: 'flex',
    flexDirection: 'column',
      wordWrap: 'break-word',
    justify: 'center',
    width: '30%',
    paddingRight: '40px',
    marginLeft: '60px',
    marginBottom: '20px',
    marginTop: 'center',
  },
  iconsStyles: {
    marginLeft: '-28px',
    marginRight: '10px',
    position: 'absolute',
    background: 'linear-gradient(#4F84C4, #00539C)',
    top: '0',
    left: '-2px',
    border: '2px solid #ddd',
    width: '70px',
    height: '70px',
    borderRadius: '50%',
    fontSize: '25px',
    paddingTop: '20px',
    color: 'white',
  },
  titleStyle :{
    fontSize: '22px',
    fontWeight: 'bolder',
    color: '#00539c',
  },
  subTitleStyle : {
    fontSize: '18px',
  }
};


export type TimeLineVProps = {
  timelinedata?: any;
  customTimelineStyles?: any;
  contentStyles?: any;
  iconsStyles?: any;
  titleStyle?: any;
  subTitleStyle?: any;
};
const TimeLineV = (props: TimeLineVProps) => {
  const {
    timelinedata,

  } = props;
  const output: any = [];
  const getTimeline = () => {
    Array.isArray(timelinedata) &&
      timelinedata.forEach(items => {
        output.push(
          <Fragment>
            <Timeline.Item
              dot={
                <Icon
                  style={StyleSheet.flatten([useStyle.iconsStyles])}
                  icon={items.icon}
                  size="2x"
                />
              }
            >
              <div style={StyleSheet.flatten([useStyle.arrowLeft])}></div>
              <div style={StyleSheet.flatten([useStyle.contentStyles])}>
                <p style={StyleSheet.flatten([useStyle.titleStyle])}>{items.date}</p>
                <p style={StyleSheet.flatten([useStyle.subTitleStyle])}>
                  {items.status}
                </p>
              </div>
            </Timeline.Item>
          </Fragment>,
        );
      });
    return output;
  };
  return (
    <Timeline style={StyleSheet.flatten([useStyle.customTimelineStyles])}>
      {getTimeline()}
    </Timeline>
  );
};
export default TimeLineV;
