import React, {Fragment} from 'react';
import {StyleSheet} from 'react-native';
import {Timeline} from 'rsuite';
import 'rsuite/dist/styles/rsuite-default.css';

//#4F84C4"

const useStyle: any = {
  arrowLeft: {

  },
  customTimelineStyles : {
    backgroundColor:"white",
    borderRadius:"5px",
    padding:"15px"
  },
  contentStyles : {

    wordWrap: 'break-word',

  },

  titleStyle :{
    fontSize:"15px",
    fontWeight: 'bolder',
    color:"rgba(225, 204,230,10)"
  },
  subTitleStyle : {
    // fontSize: '18px',
  }
};


export type TimeLineVProps = {
  timelinedata?: any;
  customTimelineStyles?: any;
  contentStyles?: any;
  iconsStyles?: any;
  titleStyle?: any;
  subTitleStyle?: any;
};
const TimeLineV = (props: TimeLineVProps) => {
  const {
    timelinedata,

  } = props;
  const output: any = [];
  const getTimeline = () => {
    Array.isArray(timelinedata) &&
      timelinedata.forEach(items => {
        output.push(
          <Fragment>
            <Timeline.Item

            >
              <div style={StyleSheet.flatten([useStyle.arrowLeft])}></div>
              <div style={StyleSheet.flatten([useStyle.contentStyles])}>
                <p style={StyleSheet.flatten([useStyle.titleStyle])}>{items.date}</p>
                <p style={StyleSheet.flatten([useStyle.subTitleStyle])}>
                  {items.status}
                </p>
              </div>
            </Timeline.Item>
          </Fragment>,
        );
      });
    return output;
  };
  return (
    <Timeline style={StyleSheet.flatten([useStyle.customTimelineStyles])}>
      {getTimeline()}
    </Timeline>
  );
};
export default TimeLineV;
