//@ts-nocheck
import React, {useState} from 'react';
import {Table} from 'rsuite';
import 'rsuite/dist/styles/rsuite-default.css';
import config from './components/config/blueprint.json';

const { Column, HeaderCell, Cell, Pagination ,} = Table;
const bp=config.data


const getData=() => {

  const [displayLength , page] = useState(1);
  return bp.tableData.filter((v, i) => {
    const start = displayLength * (page - 1);
    const end = start + displayLength;
    return i >= start && i < end;
  });
}

  const Ptable=() =>{
    const dd=getData();
    console.log(getData())

    const [displayLength, page] = useState(1)
    return (
      <div>
        <Table height={420} data={bp.tableData} loading={false}>
          <Column width={50} align="center" fixed>
            <HeaderCell>Id</HeaderCell>
            <Cell dataKey="id" />
          </Column>
          <Column width={100} fixed>
            <HeaderCell>First Name</HeaderCell>
            <Cell dataKey="firstName" />
          </Column>
          <Column width={100}>
            <HeaderCell>Last Name</HeaderCell>
            <Cell dataKey="lastName" />
          </Column>
          <Column width={200}>
            <HeaderCell>City</HeaderCell>
            <Cell dataKey="city" />
          </Column>
          <Column width={200} flexGrow={1}>
            <HeaderCell>Company Name</HeaderCell>
            <Cell dataKey="companyName" />
          </Column>
        </Table>
        <Pagination
          lengthMenu={[
            {
              value: 10,
              label: 10
            },
            {
              value: 20,
              label: 20
            }
          ]}
          activePage={1}
          displayLength={displayLength}
          total={bp.tableData.length}

        />
      </div>
    );
  }

export default Ptable;
