//@ts-nocheck
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import React from 'react';
import {StyleSheet, Text} from 'react-native';


type priceCardprops={

  planNameStylesStyles?:any
  amountStyles?:any
  buttonStyles?:any
  buttonPosition?:any
  listStyles?:any
  pricingTable:any[]
  titleStyles?:any
}

export const PriceCardDisplayV = (props:priceCardprops) => {
const {
        planNameStylesStyles,
        amountStyles,
        buttonStyles,
        buttonPosition,
        listStyles,
        pricingTable,
        titleStyles  } = props;

  const _getServices=(bodyContent:any[])=>{
    const _data:any=[];
     // eslint-disable-next-line no-unused-expressions
    Array.isArray(bodyContent) && bodyContent.forEach(item=>{
      _data.push(

        <ul  style={StyleSheet.flatten([listStyles])}>
          <li >{item?.li}</li>
        </ul>
      )
     });
     return _data;
    }


  const _getPriceCards = () => {
    const _tempData:any = [];
  // eslint-disable-next-line no-unused-expressions
    Array.isArray(pricingTable) && pricingTable.forEach(item=>{
      _tempData.push(
        <Grid item xs={11} sm={6} md={3} >
          <Paper  style={{backgroundImage:item?.backgroundColor,height:'100%'}} >
            <header>
              <Text style={StyleSheet.flatten([planNameStylesStyles])}>{item?.header}</Text>
            </header>

            <Text style={StyleSheet.flatten([titleStyles])}>{item?.title}</Text>
            <br/>
            <br/>
             <Text>{item?.amountTitle}</Text>
             <br/>
             <Text style={StyleSheet.flatten([amountStyles])}>{item?.amount}</Text>
             <br/>
             <br/>
             <br/>

             <Text>Includes</Text>
             {_getServices(item?.bodyContent)}

            <footer style={StyleSheet.flatten([buttonPosition])} >
              <button style={StyleSheet.flatten([buttonStyles])}>{item?.buttonTitle}
            </button>
            </footer>
            <div>
            </div>
           </Paper>
        </Grid>
      )
     });
    return _tempData;
  }

  return(
    <Grid container justify={"center"} alignItems={"center"}  >
      <Grid item xs={11}>
      <Grid container justify={"center"} spacing={4} >
      {_getPriceCards()}
      </Grid>
      </Grid>
    </Grid>
  )
}
